package week1.day2;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int maxRange = 10;

		for (int i = 0; i <= maxRange; i++) {

			if (i % 3 == 1) {

				System.out.println("Odd Number: " + i);

			}

		}

	}

}
